import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";

const Cart = () => {
  const { cart } = useSelector((state) => state);
  console.log("Printing Cart");
  console.log(cart);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce((acc, curr) => acc + curr.price, 0));
  }, [cart]);

  return (
    <div className="flex flex-col m-5 justify-center items-center">
      {cart.length > 0 ? (
        <div className="flex flex-row">
          <div className="w-3/4">
            {cart.map((item, index) => (
              <CartItem key={item.id} item={item} itemIndex={index} />
            ))}
          </div>

          <div className="flex flex-col justify-between w-1/4 p-4">
            <div className="flex flex-col">
              <div className="text-xl font-semibold mb-2">Your Cart</div>
              <div className="text-xl font-semibold mb-2">Summary</div>
              <p>
                <span className="text-gray-600">Total Items: {cart.length}</span>
              </p>
            </div>

            <div className="mt-auto">
              <p className="text-xl font-semibold mb-2">Total Amount: ${totalAmount}</p>
              <button className="bg-blue-500 text-white py-2 px-4 rounded">
                Check Out Now
              </button>
            </div>
          </div>
        </div>
      ) : (
        <div className="text-center">
          <h1 className="text-2xl font-semibold mb-4">Cart Empty</h1>
          <Link to={"/"}>
            <button className="bg-green-500 text-white py-2 px-4 rounded">
              Shop Now
            </button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default Cart;
